import React, { useState, useEffect } from "react";
import CreateTicket from "../../Shared/CreateTicket";
import endpoints from "../../api/endpoints";
import axios from "axios";
import io from "socket.io-client";

const handleLogout = () => {
  localStorage.clear();
  window.location.href = "/";
};

function UserDashboard() {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [socket, setSocket] = useState(null);
  const [username, setUsername] = useState("");

  useEffect(() => {
    // Get username
    const username = localStorage.getItem("username");
    setUsername(username);

    // Initialize WebSocket connection
    const socket = io("http://localhost:8000");
    setSocket(socket);

    // Listen for the "ticketCreated" event
    socket.on("ticketCreated", (newTicket) => {
      console.log("New ticket received:", newTicket);
      setTickets((prevTickets) => [newTicket, ...prevTickets]);
    });

    // Fetch tickets on mount
    fetchTickets();

    // Cleanup on unmount
    return () => {
      socket.disconnect();
      console.log("WebSocket disconnected");
    };
  }, []);

  const fetchTickets = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(endpoints.GET_TICKETS, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setTickets(response.data);
    } catch (error) {
      console.error("Error fetching tickets:", error.message);
    }
  };

  return (
    <div>
      <header className="dashboardHeader">
        <div className="headerLeft">Welcome, {username || "[Username]"}</div>
        <h3 className="headerCenter">User Dashboard</h3>
        <button className="logoutButton" onClick={handleLogout}>
          Logout
        </button>
      </header>
      <hr className="divider" />

      <div className="createTicketSection">
        <CreateTicket socket={socket} />
      </div>
      <hr className="divider" />

      <div className="ticketsList">
        <h3 className="sectionHeader">My Tickets</h3>
        {tickets.length > 0 ? (
          <ul className="ticketList">
            {tickets.map((ticket) => (
              <li key={ticket._id}>{ticket.title}</li>
            ))}
          </ul>
        ) : (
          <p>No tickets found.</p>
        )}
      </div>
    </div>
  );
}

export default UserDashboard;
