    <div>
      <h1>Welcome to the Agent Dashboard</h1>
      <p>Here, agents can view and manage their assigned tickets.</p>
      <button onClick={handleLogout}>Logout</button>
      <button onClick={fetchTickets}></button>
    </div>
  );
}

export default AgentDashboard;
