import React, { useState } from "react";
import axios from "axios";
import endpoints from "../api/endpoints";

function CreateTicket() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [successMessage, setSuccessMessage] = useState(""); // Add success message state

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = { title, description, category };
    const token = localStorage.getItem("token");

    try {
      const response = await axios.post(endpoints.CREATE_TICKET, data, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      console.log(response.data); // Log the success message

      // Set success message
      setSuccessMessage("Ticket created successfully!");

      // Clear form fields
      setTitle("");
      setDescription("");
      setCategory("");

      // Clear success message after a few seconds
      setTimeout(() => setSuccessMessage(""), 3000);
    } catch (error) {
      if (error.response) {
        console.error(error.response.data.message); // Backend error
      } else {
        console.error("Network or server error:", error.message);
      }
    }
  };

  return (
    <div className="createTicketSection">
      {/* Success Message */}
      {successMessage && (
        <div className="centeredToastMessage">{successMessage}</div>
      )}

      {/* Create Ticket Form */}
      <form className="createTicketForm" onSubmit={handleSubmit}>
        <h3>Create a Ticket</h3>
        <label>Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <label>Description:</label>
        <textarea
          rows="5" // Increased row height
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>

        <label>Category:</label>
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        >
          <option value="" disabled>
            Select a category
          </option>
          <option value="Technical Support">Technical Support</option>
          <option value="General Inquiry">General Inquiry</option>
          <option value="Sales">Sales</option>
        </select>

        {/* Button with margin */}
        <button type="submit" className="createTicketButton">
          Create Ticket
        </button>
      </form>
    </div>
  );
}

export default CreateTicket;
