import React, { useState, useEffect } from "react";
import axios from "axios";
import { io } from "socket.io-client";
import endpoints from "../api/endpoints";

const socket = io("http://localhost:8000"); // Ensure this matches your backend

const handleLogout = () => {
  localStorage.clear();
  window.location.href = "/"; // Redirect to homepage
};

function AdminDashboard() {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [showCreateTicket, setShowCreateTicket] = useState(false); // Toggle for ticket creation
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");

  useEffect(() => {
    // Fetch tickets on component mount
    fetchTickets();

    // Listen for status updates from WebSocket
    socket.on("statusUpdated", (updatedTicket) => {
      setTickets((prevTickets) =>
        prevTickets.map((ticket) =>
          ticket._id === updatedTicket._id ? updatedTicket : ticket
        )
      );
    });

    // Cleanup WebSocket listener on unmount
    return () => {
      socket.off("statusUpdated");
    };
  }, []);

  const fetchTickets = async () => {
    try {
      const token = localStorage.getItem("token");
      const role = localStorage.getItem("role");

      const response = await axios.get(endpoints.GET_TICKETS, {
        headers: {
          Authorization: `Bearer ${token}`,
          Role: role,
        },
      });
      setTickets(response.data);
    } catch (error) {
      console.error("Error fetching tickets:", error.message);
    }
  };

  const fetchTicketDetails = async (ticketId) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(endpoints.GET_TICKET_BY_ID(ticketId), {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setSelectedTicket(response.data);
    } catch (error) {
      console.error("Error fetching ticket details:", error.message);
    }
  };

  const deleteTicket = async (ticketId) => {
    const confirmDelete = window.confirm(
      "Are you sure you want to delete this ticket?"
    );

    if (!confirmDelete) {
      return;
    }

    try {
      const token = localStorage.getItem("token");
      await axios.delete(endpoints.DELETE_TICKET(ticketId), {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setTickets(tickets.filter((ticket) => ticket._id !== ticketId));
      alert("Ticket deleted successfully.");
    } catch (error) {
      console.error("Error deleting ticket:", error.message);
      alert("Failed to delete ticket.");
    }
  };

  const handleCreateTicket = async () => {
    try {
      const token = localStorage.getItem("token");

      const data = { title, description, category };
      await axios.post(endpoints.CREATE_TICKET, data, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      alert("Ticket created successfully!");
      setShowCreateTicket(false); // Hide the create ticket form
      fetchTickets(); // Refresh tickets
    } catch (error) {
      console.error("Error creating ticket:", error.message);
      alert("Failed to create ticket.");
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <button onClick={fetchTickets}>View All Tickets</button>
      <button onClick={() => setShowCreateTicket(!showCreateTicket)}>
        {showCreateTicket ? "Cancel Ticket Creation" : "Create Ticket"}
      </button>

      {/* Create Ticket Form */}
      {showCreateTicket && (
        <div>
          <h2>Create a New Ticket</h2>
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <input
            type="text"
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <input
            type="text"
            placeholder="Category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />
          <button onClick={handleCreateTicket}>Submit Ticket</button>
        </div>
      )}

      <ul>
        {tickets.map((ticket) => (
          <li key={ticket._id}>
            <strong>Title:</strong> {ticket.title} <br />
            <strong>Status:</strong> {ticket.status} <br />
            <button onClick={() => fetchTicketDetails(ticket._id)}>
              View Details
            </button>
            <button onClick={() => deleteTicket(ticket._id)}>Delete</button>
          </li>
        ))}
      </ul>

      {selectedTicket && (
        <div>
          <h2>Ticket Details</h2>
          <p>
            <strong>Title:</strong> {selectedTicket.title}
          </p>
          <p>
            <strong>Description:</strong> {selectedTicket.description}
          </p>
          <p>
            <strong>Category:</strong> {selectedTicket.category}
          </p>
          <p>
            <strong>Status:</strong> {selectedTicket.status}
          </p>
          <p>
            <strong>Created By:</strong>{" "}
            {selectedTicket.createdBy?.username || "N/A"}
          </p>
        </div>
      )}

      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default AdminDashboard;
