import React from "react";
import Login from "./Login";
import Register from "./Register";

function Home() {
  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      {/* Header */}
      <header>
        <h3>Welcome to the Ticketing System</h3>
      </header>
      <hr
        style={{ margin: "20px auto", width: "80%", border: "1px solid #ccc" }}
      />

      {/* Main Container */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "50px",
          margin: "0 auto",
        }}
      >
        {/* Login Section */}
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "flex-start",
          }}
        >
          <h4 style={{ marginBottom: "10px" }}>Login</h4>
          <div style={{ marginBottom: "15px" }}>
            <label style={{ display: "block" }}>Email:</label>
            <input type="email" />
          </div>
          <div style={{ marginBottom: "15px" }}>
            <label style={{ display: "block" }}>Password:</label>
            <input type="password" />
          </div>
          <button style={{ alignSelf: "flex-end" }}>Login</button>
        </div>

        {/* Register Section */}
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "flex-start",
          }}
        >
          <h4 style={{ marginBottom: "10px" }}>Register</h4>
          <div style={{ marginBottom: "15px" }}>
            <label style={{ display: "block" }}>Username:</label>
            <input type="text" />
          </div>
          <div style={{ marginBottom: "15px" }}>
            <label style={{ display: "block" }}>Email:</label>
            <input type="email" />
          </div>
          <div style={{ marginBottom: "15px" }}>
            <label style={{ display: "block" }}>Password:</label>
            <input type="password" />
          </div>
          <button style={{ alignSelf: "flex-end" }}>Register</button>
        </div>
      </div>
    </div>
  );
}

export default Home;
