import React, { useState, useEffect } from "react";
import axios from "axios";
import endpoints from "../../api/endpoints";
import io from "socket.io-client";

const handleLogout = () => {
  localStorage.clear();
  window.location.href = "/"; // Redirect to homepage
};

function AdminDashboard() {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [socket, setSocket] = useState(null);
  const [successMessage, setSuccessMessage] = useState(""); // Success message state

  // Initialize WebSocket connection
  useEffect(() => {
    const newSocket = io("http://localhost:8000"); // WebSocket server
    setSocket(newSocket);

    newSocket.on("statusUpdated", (updatedTicket) => {
      if (!updatedTicket) return;

      setTickets((prevTickets) =>
        prevTickets.map((ticket) =>
          ticket._id === updatedTicket._id ? updatedTicket : ticket
        )
      );

      setSelectedTicket((prevSelected) =>
        prevSelected && prevSelected._id === updatedTicket._id
          ? updatedTicket
          : prevSelected
      );
    });

    return () => newSocket.disconnect();
  }, []);

  // Fetch tickets on mount
  useEffect(() => {
    fetchTickets();
  }, []);

  const fetchTickets = async () => {
    try {
      const token = localStorage.getItem("token");
      const role = localStorage.getItem("role");

      const response = await axios.get(endpoints.GET_TICKETS, {
        headers: {
          Authorization: `Bearer ${token}`,
          Role: role,
        },
      });
      setTickets(response.data);
    } catch (error) {
      console.error("Error fetching tickets:", error.message);
    }
  };

  const handleTicketCreated = (message) => {
    setSuccessMessage(message); // Set the success message
    setTimeout(() => setSuccessMessage(""), 3000); // Clear after 3 seconds
    fetchTickets(); // Refresh tickets
  };

  return (
    <div>
      {/* Logout Button */}
      <button onClick={handleLogout} className="logoutButton">
        Logout
      </button>

      {/* Header */}
      <header className="dashboardHeader">
        <h3>Admin Dashboard</h3>
      </header>
      <hr className="divider" />

      {/* Success Message */}
      {successMessage && (
        <div
          className="successMessage"
          style={{ textAlign: "center", margin: "10px 0", color: "green" }}
        >
          {successMessage}
        </div>
      )}

      {/* Create Ticket Section */}
      <div className="createTicketSection">
        <CreateTicket onTicketCreated={handleTicketCreated} />
      </div>
      <hr className="divider" />

      {/* Tickets and Details */}
      <div className="dashboardContainer">
        {/* Ticket List Section */}
        <div className="ticketsList">
          <h3 className="sectionHeader">Ticket List</h3>
          <ul className="ticketList">
            {tickets.length > 0 ? (
              tickets.map((ticket) => (
                <li key={ticket._id} className="ticketItem">
                  <strong>Title:</strong> {ticket.title}
                  <br />
                  <strong>Status:</strong>{" "}
                  <select
                    value={ticket.status}
                    onChange={(e) =>
                      handleUpdateTicket(ticket._id, e.target.value)
                    }
                  >
                    <option value="Waiting">Waiting</option>
                    <option value="In Progress">In Progress</option>
                    <option value="Resolved">Resolved</option>
                  </select>
                  <br />
                  <button onClick={() => fetchTicketDetails(ticket._id)}>
                    View Details
                  </button>
                  <button onClick={() => deleteTicket(ticket._id)}>
                    Delete
                  </button>
                </li>
              ))
            ) : (
              <p>No tickets available</p>
            )}
          </ul>
        </div>

        {/* Ticket Details Section */}
        <div className="ticketDetails">
          <h3 className="sectionHeader">Ticket Details</h3>
          {selectedTicket ? (
            <div>
              <p>
                <strong>Title:</strong> {selectedTicket.title}
              </p>
              <p>
                <strong>Description:</strong> {selectedTicket.description}
              </p>
              <p>
                <strong>Category:</strong> {selectedTicket.category}
              </p>
              <p>
                <strong>Status:</strong> {selectedTicket.status}
              </p>
              <p>
                <strong>Created By:</strong>{" "}
                {selectedTicket.createdBy?.username || "N/A"}
              </p>
            </div>
          ) : (
            <p>Select a ticket to view details</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
