import React, { useState, useEffect } from "react";
import axios from "axios";
import endpoints from "../../api/endpoints";
import io from "socket.io-client";

const handleLogout = () => {
  localStorage.clear();
  window.location.href = "/"; // Redirect to homepage
};

function AgentDashboard() {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [socket, setSocket] = useState(null);

  // Initialize WebSocket connection
  useEffect(() => {
    const newSocket = io("http://localhost:8000");
    setSocket(newSocket);

    // Listen for the "newTicketCreated" event
    newSocket.on("newTicketCreated", (newTicket) => {
      console.log("New ticket received:", newTicket);

      // Get the agent's department from Local Storage
      const agentDepartment = localStorage.getItem("department");

      // Check if the ticket category matches the agent's department
      if (newTicket.category === agentDepartment) {
        console.log("Ticket matches department, adding to list");

        // Add the new ticket to the existing list
        setTickets((prevTickets) => [newTicket, ...prevTickets]);
      } else {
        console.log("Ticket does not match department, ignoring");
      }
    });

    // Cleanup WebSocket on component unmount
    return () => {
      newSocket.disconnect();
      console.log("WebSocket disconnected in AgentDashboard");
    };
  }, []);

  useEffect(() => {
    // Initialize WebScoket connection
    const newSocket = io("https://localhost:8000");
    setSocket(newSocket);

    // Listen for the "newTicketCreated" event
    newSocket.on("newTicketCreated", (newTicket) => {
      console.log("New ticket received:", newTicket);

      // Get the agent's department from Local Storage
      const agentDepartment = localStorage.getItem("department");
    });

    // Fetch tickets on mount
    fetchTickets();
  }, []);

  // Fetch all tickets assigned to the agent
  const fetchTickets = async () => {
    try {
      const token = localStorage.getItem("token");
      const role = localStorage.getItem("role");

      const response = await axios.get(endpoints.GET_TICKETS, {
        headers: {
          Authorization: `Bearer ${token}`,
          Role: role,
        },
      });
      setTickets(response.data);
    } catch (error) {
      console.error("Error fetching tickets:", error.message);
    }
  };

  // Update ticket status
  const handleUpdateTicket = async (ticketId, status) => {
    try {
      const token = localStorage.getItem("token");

      // Send the PUT request to update the ticket status
      const response = await axios.put(
        endpoints.UPDATE_TICKET(ticketId),
        { status },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.status === 200) {
        // Emit the WebSocket event
        const updatedTicket = response.data.updatedTicket;
        console.log(
          "Emitting statusUpdated event for:",
          response.data.updatedTicket
        );
        socket.emit("statusUpdated", updatedTicket);

        // Update the UI with the updated ticket
        setTickets((prevTickets) =>
          prevTickets.map((ticket) =>
            ticket._id === updatedTicket._id ? updatedTicket : ticket
          )
        );
        console.log("Ticket status updated successfully!");
      } else {
        throw new Error("Failed to update ticket.");
      }
    } catch (error) {
      console.error("Error updating ticket:", error.message);
    }
  };

  const fetchTicketDetails = async (ticketId) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(endpoints.GET_TICKET_BY_ID(ticketId), {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setSelectedTicket(response.data);
    } catch (error) {
      console.error("Error fetching ticket details:", error.message);
    }
  };

  return (
    <div>
      {/* Logout Button */}
      <button onClick={handleLogout} className="logoutButton">
        Logout
      </button>

      {/* Header Section */}
      <header className="dashboardHeader">
        <h3>Agent Dashboard</h3>
      </header>
      <hr className="divider" />

      {/* Dashboard Content */}
      <div className="dashboardContainer">
        {/* Assigned Tickets Section */}
        <div className="ticketsList">
          <h3 className="sectionHeader">Assigned Tickets</h3>
          <ul className="ticketList">
            {tickets.map((ticket) => (
              <li key={ticket._id} className="ticketItem">
                <strong>Title:</strong> {ticket.title} <br />
                <strong>Status:</strong>{" "}
                <select
                  value={ticket.status}
                  onChange={(e) =>
                    handleUpdateTicket(ticket._id, e.target.value)
                  }
                >
                  <option value="Waiting">Waiting</option>
                  <option value="In Progress">In Progress</option>
                  <option value="Resolved">Resolved</option>
                </select>
                <br />
                <button onClick={() => fetchTicketDetails(ticket._id)}>
                  View Details
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Ticket Details Section */}
        <div className="ticketDetails">
          <h3 className="sectionHeader">Ticket Details</h3>
          {selectedTicket ? (
            <div>
              <p>
                <strong>Title:</strong> {selectedTicket.title}
              </p>
              <p>
                <strong>Description:</strong> {selectedTicket.description}
              </p>
              <p>
                <strong>Category:</strong> {selectedTicket.category}
              </p>
              <p>
                <strong>Status:</strong> {selectedTicket.status}
              </p>
              <p>
                <strong>Created By:</strong>{" "}
                {selectedTicket.createdBy?.username || "N/A"}
              </p>
            </div>
          ) : (
            <p>Select a ticket to view details.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default AgentDashboard;
