import React, { useState } from "react";
import axios from "axios";
import endpoints from "../api/endpoints";

function CreateTicket({ onTicketCreated }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = { title, description, category };
    const token = localStorage.getItem("token");

    try {
      // Send the POST request
      const response = await axios.post(endpoints.CREATE_TICKET, data, {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Display the success message
      setSuccessMessage("Ticket created successfully!");

      // Clear form fields
      setTitle("");
      setDescription("");
      setCategory("");

      // Clear the message after 3 seconds
      setTimeout(() => setSuccessMessage(""), 3000);
    } catch (error) {
      console.error("Error creating ticket:", error.message);
    }
  };

  return (
    <div style={{ display: "flex", alignItems: "flex-start" }}>
      {/* Form Section */}
      <div className="createTicketForm" style={{ flex: "1" }}>
        <h3>Create a Ticket</h3>
        <form onSubmit={handleSubmit}>
          <div className="formGroup">
            <label>Title:</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className="formGroup">
            <label>Description:</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            ></textarea>
          </div>
          <div className="formGroup">
            <label>Category:</label>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            >
              <option value="" disabled>
                Select a category
              </option>
              <option value="Technical Support">Technical Support</option>
              <option value="General Inquiry">General Inquiry</option>
              <option value="Sales">Sales</option>
            </select>
          </div>
          <button type="submit">Create Ticket</button>
        </form>
      </div>

      {/* Success Message Section */}
      {successMessage && (
        <div
          style={{
            marginLeft: "20px", // Add space between the form and the message
            flex: "0 0 200px", // Fixed width for the message
            textAlign: "center",
            fontWeight: "bold",
            alignSelf: "center",
          }}
        >
          {successMessage}
        </div>
      )}
    </div>
  );
}

export default CreateTicket;
